sys_variable_value.value:
(function execute(inputs, outputs) {
if(inputs.address1){
    outputs.finaladdress = inputs.address1.replaceAll(" ","+");
}else if(inputs.address2){
    outputs.finaladdress = inputs.address2.replaceAll(" ","+");
}
})(inputs, outputs);

