sys_hub_step_ext_output.calculation:
(function calculatedFieldValue(current) {

	// Add your code here
	return '';  // return the calculated value

})(current);

sys_variable_value.value:
(function execute(inputs, outputs) {


gs.info("vhttpStatus: " + inputs.http_status);
gs.info("vhttpStatus: " + inputs.response_body);

if(inputs.http_status == '200' || inputs.http_status == '201' || inputs.http_status == '202'){
var responseBody = JSON.parse(inputs.response_body);
gs.info("vhttpStatus: " + responseBody.address);

var cat_perils = new GlideRecord('x_ltin2_uw_ai_location_cat_perils');
cat_perils.get(inputs.locationcatperils);
//mapping starts here

cat_perils.earthquake_score = responseBody.earthquake.score;
cat_perils.earthquake_details = JSON.stringify(responseBody.earthquake);
cat_perils.hurricane_details = JSON.stringify(responseBody.enhanced_hurricane_params);
cat_perils.hurricane_score = responseBody.enhanced_hurricane_params.score;
cat_perils.wild_fire_details = JSON.stringify(responseBody.enhanced_wildfire);
cat_perils.wild_fire_score = responseBody.enhanced_wildfire.score;

if(responseBody.sinkhole.score){
    cat_perils.sinkhole = true;
    cat_perils.sinkhole_details = JSON.stringify(responseBody.sinkhole);
    cat_perils.sinkhole_score = responseBody.sinkhole.score;
}
if(responseBody.convection_storm.score){
cat_perils.convection_storm = true;
cat_perils.convection_storm_details = responseBody.convection_storm.text;
cat_perils.convection_storm_score = responseBody.convection_storm.score;
}
if(responseBody.enhanced_tornado_params.score){
cat_perils.tornado = true;
cat_perils.tornado_score = responseBody.enhanced_tornado_params.score;
cat_perils.tornado_details = JSON.stringify(responseBody.enhanced_tornado_params);
}
if(responseBody.mold_index.score){
cat_perils.mold_index = true;
cat_perils.mold_index_score = responseBody.mold_index.score;
cat_perils.mold_index_details = JSON.stringify(responseBody.mold_index);
}
if(responseBody.ice_dam_index.score){
cat_perils.ice_dam_index = true;
cat_perils.ice_dam_index_score = responseBody.ice_dam_index.score;
cat_perils.ice_dam_index_details = JSON.stringify(responseBody.ice_dam_index);
}
if(responseBody.frozen_pipe_index.score){
cat_perils.frozen_pipe_index = true;
cat_perils.frozen_pipe_index_score = responseBody.frozen_pipe_index.score;
cat_perils.frozen_pipe_index_details = JSON.stringify(responseBody.frozen_pipe_index);
}
if(responseBody.enhanced_hazardhub_flood.score){
cat_perils.hazardhub_flood = true;
cat_perils.hazardhub_flood_score = responseBody.enhanced_hazardhub_flood.score;
cat_perils.hazardhub_flood_details = responseBody.enhanced_hazardhub_flood.text;
}
if(responseBody.fema_all_flood.score){
cat_perils.fema_flood = true;
cat_perils.fema_flood_score = responseBody.fema_all_flood.score;
cat_perils.fema_flood_details = responseBody.fema_all_flood.text;
}
if(responseBody.coast_distance.distance){
cat_perils.coast_distance = true;
cat_perils.coast_distance_score = responseBody.coast_distance.distance;
cat_perils.coast_distance_details = responseBody.coast_distance.units;
}
if(responseBody.forcible_robbery.score){
cat_perils.forcible_robbery = true;
cat_perils.forcible_robbery_score = responseBody.forcible_robbery.score;
cat_perils.forcible_robbery_details = responseBody.forcible_robbery.text;
}
if(responseBody.burglary.score){
cat_perils.burglary = true;
cat_perils.burglary_score = responseBody.burglary.score;
cat_perils.burglary_details = responseBody.burglary.text;
}
if(responseBody.crime.score){
cat_perils.crime = true;
cat_perils.crime_score = responseBody.crime.score;
cat_perils.crime_details = responseBody.crime.text;
}
if(responseBody.toxic_release_facilities.score){
cat_perils.toxic_release_facilities = true;
cat_perils.toxic_release_facilities_score = responseBody.toxic_release_facilities.score;
cat_perils.toxic_release_facilities_details = responseBody.toxic_release_facilities.text;
}
if(responseBody.police_stations.name){
cat_perils.police_stations = true;
cat_perils.police_stations_details = JSON.stringify(responseBody.police_stations);
}


//mapping ends here
cat_perils.update();

}

})(inputs, outputs);

